// LoginComponent.js
import React, { useState } from 'react';
import { View, Text, TextInput, Button, StyleSheet, Alert } from 'react-native';

const LoginComponent = ({ navigation }) => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');

  const handleLogin = () => {
    // Đây là nơi để xử lý logic đăng nhập
    // Trong ví dụ này, kiểm tra nếu tên người dùng là 'user' và mật khẩu là 'password'
    if (username === 'user' && password === 'password') {
      Alert.alert('Thông báo', 'Đăng nhập thành công!');
      // Chuyển đến màn hình chính (trang chủ)
      navigation.navigate('Home');
    } else {
      Alert.alert('Thông báo', 'Đăng nhập thất bại. Vui lòng kiểm tra lại tên người dùng và mật khẩu.');
    }
  };

  return (
    <View style={styles.container}>
      <Text style={styles.label}>Tên người dùng:</Text>
      <TextInput
        style={styles.input}
        value={username}
        onChangeText={(text) => setUsername(text)}
        placeholder="Nhập tên người dùng"
      />
      <Text style={styles.label}>Mật khẩu:</Text>
      <TextInput
        style={styles.input}
        value={password}
        onChangeText={(text) => setPassword(text)}
        placeholder="Nhập mật khẩu"
        secureTextEntry
      />
      <Button title="Đăng nhập" onPress={handleLogin} />
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: 'center',
    padding: 20,
  },
  label: {
    fontSize: 18,
    marginBottom: 5,
  },
  input: {
    height: 40,
    borderColor: 'gray',
    borderWidth: 1,
    marginBottom: 10,
    paddingHorizontal: 10,
  },
});

export default LoginComponent;
