// LoginComponent.js
import React, { useState } from 'react';
import { View, Text, TextInput, Button, StyleSheet, Alert, TouchableOpacity } from 'react-native';

const LoginComponent = ({ navigation }) => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');

  const handleLogin = () => {
    // Đây là nơi để bạn xử lý logic đăng nhập, kiểm tra tài khoản và mật khẩu
    // Sau khi đăng nhập thành công, bạn có thể thực hiện các thao tác khác hoặc chuyển đến màn hình khác
    Alert.alert('Thông báo', 'Đăng nhập thành công!');
    // Ví dụ: Chuyển đến màn hình chính (trang chủ)
    navigation.navigate('Home');
  };

  const handleGoBack = () => {
    // Quay lại trang chủ khi người dùng click nút "Quay lại"
    navigation.goBack();
  };

  return (
    <View style={styles.container}>
      <Text style={styles.label}>Tên người dùng:</Text>
      <TextInput
        style={styles.input}
        value={username}
        onChangeText={(text) => setUsername(text)}
        placeholder="Nhập tên người dùng"
      />
      <Text style={styles.label}>Mật khẩu:</Text>
      <TextInput
        style={styles.input}
        value={password}
        onChangeText={(text) => setPassword(text)}
        placeholder="Nhập mật khẩu"
        secureTextEntry
      />
      <Button title="Đăng nhập" onPress={handleLogin} />
      <TouchableOpacity onPress={handleGoBack}>
        <Text style={styles.linkText}>Quay lại trang chủ</Text>
      </TouchableOpacity>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: 'center',
    padding: 20,
  },
  label: {
    fontSize: 18,
    marginBottom: 5,
  },
  input: {
    height: 40,
    borderColor: 'gray',
    borderWidth: 1,
    marginBottom: 10,
    paddingHorizontal: 10,
  },
  linkText: {
    color: 'blue',
    marginTop: 10,
    textAlign: 'center',
  },
});

export default LoginComponent;
